class Pessoa extends CI_Controller{
    function __construct()
    {
        parent::__construct();

        $this->load->model('Pessoa_model');
        $this->load->model('Endereco_model');
        $this->load->model('Contato_model');
        $this->load->model('PessoaFisica_model');
